import Layout from "../components/Layout";
import { useTrail, animated } from "react-spring";

function Contact() {
  const title = "Contact me!".split(" ");
  const config = { mass: 10, tension: 600, friction: 200 };

  const trail = useTrail(title.length, {
    config,
    opacity: 1,
    x: 20,
    height: 80,
    from: { opacity: 0, x: 20, height: 0 }
  });

  return (
    <Layout>
      <h1>
        {trail.map(({ x, height, ...rest }, index) => (
          <animated.span
            key={title[index]}
            style={{
              ...rest,
              transform: x.interpolate(x => `translate3d(0, ${x}px,0)`)
            }}
          >
            {`${title[index]} `}
          </animated.span>
        ))}
      </h1>
      <form action="/">
        <label>Name:</label>
        <input type="text" id="name" />

        <label>Email:</label>
        <input type="email" id="email" />

        <label>Subject:</label>
        <input type="text" id="subject" />

        <label>Text:</label>
        <textarea id="text" />

        <button type="submit" className="work-btn">
          Send
        </button>
      </form>
      <style jsx>
        {`
          form {
            display: grid;
            grid-gap: 1em;
            margin: 0 auto;
            max-width: 90%;
            justify-content: center;
            flex-direction: column;
          }

          label {
            justify-self: start;
            font-size: 1.2em;
            font-weight: bolder;
            color: #718096;
          }

          input {
            border: none;
            background: #e2e8f0;
            justify-self: end;
            width: 50vw;
            color: #4a5568;
            font-size: 1em;
            font-weight: bolder;
            height: 2em;
            padding: 0.5em;
          }

          button {
            width: max-content;
            justify-self: center;
            background: #777;
            padding: 1vmax 2vmax;
            color: #f7fafc;
            font-size: 1.2em;
            font-weight: 600;
            border: none;
            cursor: pointer;
          }

          textarea {
            border: none;
            background: #e2e8f0;
            height: 5em;
          }
        `}
      </style>
    </Layout>
  );
}

export default Contact;
