import Layout from "../components/Layout";
import Link from "next/link";
import { useTrail } from "react-spring";

function HomePage() {
  const title = "Hi there! thanks for stopping by.".split("");
  const config = { mass: 10, tension: 600, friction: 200 };

  const trail = useTrail(title.length, {
    config,
    opacity: 1,
    x: 20,
    height: 80,
    from: { opacity: 0, x: 20, height: 0 }
  });
  return (
    <Layout>
      <h1>{trail.map(({x, height, ...rest}, index) => (
        <animated.span
        key={title[index]}
        style={{
          ...rest,
          transform: x.interpolate(x => `translate3d(0, ${x}px,0)`)
        }}>


        </animated.span>
      ))}</h1>
      <div className="about-text">
        <img src="/static/portrait-brian-bawuah.jpg"></img>
        <p>
          I am a 23 year young student, who found his passion for developing and
          designing applications in the beautiful and complex world of the
          inter-webs. At the moment, my main focus is on mastering the front-end
          frameworks as React.js and the back-end development in Node.js.
          However, in the long run I would like to learn more about how to
          implement different libraries, AI and machine learning in my work.
        </p>
        <Link href="/work">
          <a className="work-btn">My work</a>
        </Link>
      </div>
      <style jsx>
        {`
          img {
            max-width: 40%;
            justify-self: center;
          }
          .about-text {
            color: #777;
            text-align: left;
            max-width: 800px;
            margin: 0 auto 100px;
            padding: 20px;
            display: grid;
            grid-template-column: 1fr 1fr;
            grid-gap: 1em;
          }

          .work-btn {
            position: relative;
            background: #777;
            padding: 1vmax 2vmax;
            color: #f7fafc;
            // width: max-content;
            justify-self: center;
            text-decoration: none;
            font-weight: 600;
            font-size: 1.2em;
            overflow: hidden;
          }
        `}
      </style>
    </Layout>
  );
}

export default HomePage;
