import Layout from "../components/Layout";
import Link from "next/link";

function HomePage() {
  return (
    <Layout>
      <h1>Hi there! thanks for stopping by.</h1>
      <div className="about-text">
        <img src="/static/portrait-brian-bawuah.jpg"></img>
        <p>
          I am a 23 year young student, who found his passion for developing and
          designing applications in the beautiful and complex world of the
          inter-webs. At the moment, my main focus is on mastering the front-end
          (HTML, CSS, JavaScript ) and the back-end development (Node.JS ).
          However, in the long run I would like to learn more about how to
          implement different frameworks, AI and machine learning in my work.
        </p>
        <Link href="/work">
          <a className="work-btn">Show me your work</a>
        </Link>
      </div>
      <style jsx>
        {`
          img {
            max-width: 40%;
            justify-self: center;
          }
          .about-text {
            color: #777;
            text-align: left;
            max-width: 800px;
            margin: 0 auto 100px;
            padding: 20px;
            display: grid;
            grid-template-column: 1fr 1fr;
            grid-gap: 1em;
          }

          .work-btn {
            background: #777;
            padding: 2vmax 6vmax;
            color: #f7fafc;
            width: max-content;
            justify-self: center;
            text-decoration: none;
            font-weight: 600;
          }
        `}
      </style>
    </Layout>
  );
}

export default HomePage;
