import Layout from "../components/Layout";
import { useTrail, animated } from "react-spring";

const title = ["Get", "to", "know", "me"];
const config = { mass: 5, tension: 2000, friction: 200 };

function About() {
  const trail = useTrail(title.length, {
    config,
    opacity: 1,
    x: 20,
    height: 80,
    from: { opacity: 0, x: 20, height: 0 }
  });
  return (
    <Layout>
      <h1>lol{trail.map(({x, height, ...rest}, index) => {
        <animated.span
        key={title[index]}
        style={{
          ...rest,
          transform: x.interpolate(x => `translate3d(0,${x}px,0)`)
        }}>

          {`${title[index]} `}
        </animated.span>
      })}</h1>
      <div className="about-text">
        <img src="/static/portrait-brian-bawuah.jpg"></img>
        <p>
          I am a 23 year young student, who found his passion for developing and
          designing applications in the beautiful and complex world of the
          inter-webs. At the moment, my main focus is on mastering the front-end
          (HTML, CSS, JavaScript ) and the back-end development (Node.JS ).
          However, in the long run I would like to learn more about how to
          implement different frameworks, AI and machine learning in my work.
        </p>
      </div>
      <style jsx>
        {`
          img {
            max-width: 50%;
            justify-self: center;
          }
          .about-text {
            color: #777;
            text-align: left;
            max-width: 800px;
            margin: 0 auto 100px;
            padding: 20px;
            display: grid;
            grid-template-column: 1fr 1fr;
            grid-gap: 1em;
          }
        `}
      </style>
    </Layout>
  );
}

export default About;
