import {useRouter} from 'next/router';

const router = useRouter();

export const getCodeUrl = () => {

    if (router.query.id.toLowerCase().split(" ")[0] === "bible") {
      window.location.href = "https://github.com/bbawuah/bible-app";
    }

    if (router.query.id.toLowerCase().split(" ")[0] === "chat") {
        window.location.href = "https://github.com/bbawuah/chat-app";
      }

      if (router.query.id.toLowerCase().split(" ")[0] === "flight") {
        window.location.href = "https://github.com/bbawuah/flight-app";
      }

      if (router.query.id.toLowerCase().split(" ")[0] === "weather") {
        window.location.href = "https://github.com/bbawuah/Node-3-weather-website";
      }
  };
