import Layout from "../components/Layout";
import Project from "../components/Project";
import { useTrail, animated } from "react-spring";
import Link from "next/link";

function Work() {
  const title = "Check out my work".split(" ");
  const config = { mass: 10, tension: 600, friction: 200 };

  const trail = useTrail(title.length, {
    config,
    opacity: 1,
    x: 20,
    height: 80,
    from: { opacity: 0, x: 20, height: 0 }
  });

  const PostLink = props => (
    <ul>
      <li>
        <Link href="/p/[id]" as={`/p/${props.id}`}>
          <a>{props.id}</a>
        </Link>
      </li>
      <style jsx>
        {`
          ul {
            list-style-type: none;
          }
          a {
            text-decoration: none;
            width: max-content;
            justify-self: center;
            background: #777;
            padding: 1vmax 2vmax;
            color: #f7fafc;
            font-size: 1.2em;
            font-weight: 600;
            border: none;
            cursor: pointer;
          }
        `}
      </style>
    </ul>
  );

  return (
    <Layout>
      <h1>
        {trail.map(({ x, height, ...rest }, index) => (
          <animated.span
            key={title[index]}
            style={{
              ...rest,
              transform: x.interpolate(x => `translate3d(0, ${x}px,0)`)
            }}
          >
            {`${title[index]} `}
          </animated.span>
        ))}
      </h1>
      <div className="projects">
        <div className="project-list">
          <Project>
            {/* <h2>Flight App</h2> */}
            <PostLink id="Flight App" />
          </Project>

          <Project>
            {/* <h2>Bible App</h2> */}
            <PostLink id="Bible" />
          </Project>

          <Project>
            {/* <h2>Chat App</h2> */}
            <PostLink id="Chat App" />
          </Project>

          <Project>
            {/* <h2>Weather App</h2> */}
            <PostLink id="Weather App" />
          </Project>
        </div>
      </div>
      <style jsx>{`
        .projects {
          max-width: 1000px;
          margin: 0 auto;
        }

        .project-list {
          padding: 0 30px;
          display: flex;
          flex-wrap: wrap;
          justify-content: space-between;
        }
      `}</style>
    </Layout>
  );
}

export default Work;
