import { useRouter } from "next/router";
import Layout from "../../components/Layout";
import {useTrail, animated} from 'react-spring';
import ProjectText from '../../components/Project';

export default function Project() {
  const router = useRouter();

  const title = router.query.id.split(" ");
  const config = { mass: 10, tension: 600, friction: 200 };

  const trail = useTrail(title.length, {
    config,
    opacity: 1,
    x: 20,
    height: 80,
    from: { opacity: 0, x: 20, height: 0 }
  });

  return (
    <Layout>
      <h1>{trail.map(({ x, height, ...rest }, index) => (
          <animated.span
            key={title[index]}
            style={{
              ...rest,
              transform: x.interpolate(x => `translate3d(0, ${x}px,0)`)
            }}
          >
            {`${title[index]} `}
          </animated.span>
        ))}</h1>
        <p>Here goes project content..</p>
    </Layout>
  );
}
